mapper.update:
update user_info set password=#{password},name=#{name},idcard=#{idCard},
email=#{email},
area=#{area},
gender=#{gender},
address=#{address}
where uid = #{uId}

mapper.select:
select * from user_info
where name = #{name} and password = #{password}
limit 1

